DROP DATABASE IF EXISTS `chamber_music`;
CREATE DATABASE `chamber_music`; 
USE `chamber_music`;

CREATE TABLE `work_types` (
  `work_type_id` int NOT NULL AUTO_INCREMENT,
  `description` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`work_type_id`)
);


CREATE TABLE `composers` (
  `composer_id` int NOT NULL AUTO_INCREMENT,
  `era` varchar(50) DEFAULT NULL,
  `nationality` varchar(20) DEFAULT NULL,
  `last_name` varchar(50) DEFAULT NULL,
  `first_name` varchar(50) DEFAULT NULL,
  `birth_date` date DEFAULT NULL,
  `death_date` date DEFAULT NULL,
  PRIMARY KEY (`composer_id`)
);


CREATE TABLE `works` (
  `work_id` int NOT NULL AUTO_INCREMENT,
  `composer_id` int DEFAULT NULL,
  `work_type_id` int DEFAULT NULL,
  `title` varchar(50) DEFAULT NULL,
  `key` varchar(20) DEFAULT NULL,
  `opus_no` varchar(50) DEFAULT NULL,
  `work_no` int DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  `completion_year` int DEFAULT NULL,
  PRIMARY KEY (`work_id`)
);

